import React, {Component} from "react";
import { Slide } from "react-slideshow-image";
import classes from "../css/Mobile.module.css";
import image from "../img/first.jpeg";
import image2 from "../img/second.jpeg";
import image3 from "../img/third.jpeg";
import image4 from "../img/fourth.jpeg";

class Mobile extends Component {
  constructor() {
    super();
    this.slideRef = React.createRef();
    this.back = this.back.bind(this);
    this.next = this.next.bind(this);
    this.state = {
      current: 0,
    };
  }

  back() {
    this.slideRef.current.goBack();
  }

  next() {
    this.slideRef.current.goNext();
  }

  render() {
    const properties = {
      duration: 5000,
      autoplay: false,
      transitionDuration: 500,
      arrows: false,
      infinite: true,
      easing: "ease",
      indicators: (i) => <div className="indicator">{i + 1}</div>,
    };
    const slideImages2 = [
      {
        url: image,
        title: "STAR WARS BEST OF 2022",
        content:
          "StarWars.com´s writers dive into the year´s biggest announcements, releases, and fan-favorite moments",
        button: "READ MORE",
      },
      {
        url: image2,
        title: "FROM SANTA YODA TO ROCKET SLEDS",
        content:
          "A look back at Lucasfilm´s Star Wars holiday cards through the years",
        button: "READ MORE",
      },
      {
        url: image3,
        title: "THE HIGH REPUBLIC SHOW",
        content:
          "Phase II exclusive reveals, a visit to Dark Horse comics, and more!",
        button: "WATCH NOW",
      },
      {
        url: image4,
        title: "FROM A CERTAIN POINT OF VIEW",
        content: "What´s your favorite episode of Andor(so far)?",
        button: "READ MORE",
      },
    ];
    return (
      <div className="App">
        <h3>Slide Effect</h3>
        <div className="slide-container">
          <Slide ref={this.slideRef} {...properties}>
            {slideImages2.map((slideImages2, index) => (
              <div key={index} >
                <img className={classes.imgMobile} src={slideImages2.url} />
                <div>
                    <h1>{slideImages2.title}</h1>
                    <h4></h4>
                </div>
              </div>
            ))}
          </Slide>
        </div>

        <div className="slide-container buttons">
          <button onClick={this.back} type="button">
            Go Back
          </button>
          <button onClick={this.next} type="button">
            Go Next
          </button>
        </div>
      </div>
    );
  }
}
export default Mobile;
