import React from 'react';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import classes from '../css/SlideShow.module.css';
import image1 from '../img/FirstImg.webp'

const slideImages = [
  {
    url: {image1},
    caption: 'Slide 1'
  },
  {
    url: './SecondImg.jpeg',
    caption: 'Slide 2'
  },
  {
    url: 'https://lumiere-a.akamaihd.net/v1/images/sw-best-of-2022-hero-desktop_6d0f5c68.jpeg?region=0,0,2048,870',
    caption: 'Slide 3'
  },
];

const SlideShow = () => {
    return (
      <div className="slide-container">
        <Slide>
         {slideImages.map((slideImage, index)=> (
            <div className={classes.cont} key={index}>
              <div className={classes.slideImg} style={{'backgroundImage': `url(${slideImage.url})`}}>
                <span>{slideImage.caption}</span>
              </div>
            </div>
          ))} 
        </Slide>
      </div>
    )
}
export default SlideShow;
